import React from 'react';

const SyncStatus = ({ syncStatus, offlineMode, userId }) => {
  return (
    <div className="syncStatus">
      {syncStatus === 'syncing' && <span className="badge syncing">‚ü≥ Syncing...</span>}
      {syncStatus === 'synced' && <span className="badge synced">‚úÖ Synced</span>}
      {syncStatus === 'error' && <span className="badge error">‚ö†Ô∏è Sync Error</span>}
      {offlineMode && <span className="badge offline">üì∂ Offline Mode</span>}
      {userId && <span className="badge user">üë§ {userId.slice(0, 6)}...</span>}
    </div>
  );
};

export default React.memo(SyncStatus);
