import React, { useState, useEffect } from 'react';
import { format, isBefore, parseISO, addDays, addWeeks, addMonths } from 'date-fns';
import { DragDropContext, Droppable, Draggable } from '@hello-pangea/dnd';

function App() {
  const [inputText, setInputText] = useState('');
  const [tasks, setTasks] = useState([]);
  const [dueInput, setDueInput] = useState('');
  const [filter, setFilter] = useState('all'); // all | active | completed
  const [editingId, setEditingId] = useState(null);
  const [editingText, setEditingText] = useState('');
  const [notificationsEnabled, setNotificationsEnabled] = useState(false);
  const [recurrenceInput, setRecurrenceInput] = useState('none');
  const [tagInput, setTagInput] = useState('');
  const [filterTag, setFilterTag] = useState(null);
  const [darkMode, setDarkMode] = useState(() => {
    try {
      return localStorage.getItem('todoapp_darkmode') === 'true';
    } catch { return false; }
  });
  const [searchText, setSearchText] = useState('');

  // Get unique tags from all tasks
  const allTags = [...new Set(tasks.flatMap(t => t.tags || []))];

  // Load tasks from localStorage on mount
  useEffect(() => {
    try {
      const raw = localStorage.getItem('todoapp_tasks');
      if (raw) setTasks(JSON.parse(raw));
    } catch (e) {
      console.warn('Failed to load tasks', e);
    }
  }, []);

  // Persist tasks whenever they change
  useEffect(() => {
    try {
      localStorage.setItem('todoapp_tasks', JSON.stringify(tasks));
    } catch (e) {
      console.warn('Failed to save tasks', e);
    }
  }, [tasks]);

  // Persist dark mode
  useEffect(() => {
    localStorage.setItem('todoapp_darkmode', darkMode);
    document.body.className = darkMode ? 'dark-mode' : '';
  }, [darkMode]);

  // Keyboard shortcuts
  useEffect(() => {
    const handleKeyDown = (e) => {
      // Ctrl+D or Cmd+D = toggle dark mode
      if ((e.ctrlKey || e.metaKey) && e.key === 'd') {
        e.preventDefault();
        setDarkMode(prev => !prev);
      }
      // Escape = cancel edit
      if (e.key === 'Escape' && editingId) {
        setEditingId(null);
        setEditingText('');
      }
    };
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [editingId]);

  const addTask = () => {
    if (inputText.trim()) {
      let nextDue = dueInput || null;
      const newTask = { 
        id: Date.now(), 
        text: inputText.trim(), 
        completed: false, 
        due: nextDue, 
        recurrence: recurrenceInput, 
        notified: false,
        tags: tagInput ? tagInput.split(',').map(t => t.trim()).filter(t => t) : []
      };
      setTasks(prev => [...prev, newTask]);
      setInputText('');
      setDueInput('');
      setRecurrenceInput('none');
      setTagInput('');
    } else {
      alert('Tulis dulu tugasnya!');
    }
  };  const deleteTask = (id) => {
    setTasks(prev => prev.filter(task => task.id !== id));
  };

  const toggleComplete = (id) => {
    const task = tasks.find(t => t.id === id);
    if (task && !task.completed && task.recurrence && task.recurrence !== 'none') {
      handleTaskComplete(id);
    } else {
      setTasks(prev => prev.map(t => t.id === id ? { ...t, completed: !t.completed } : t));
    }
  };

  const startEdit = (task) => {
    setEditingId(task.id);
    setEditingText(task.text);
  };

  const saveEdit = (id) => {
    if (!editingText.trim()) {
      // If empty, keep old text
      setEditingId(null);
      setEditingText('');
      return;
    }
    setTasks(prev => prev.map(t => t.id === id ? { ...t, text: editingText.trim(), notified: t.due && isBefore(parseISO(t.due), new Date()) ? t.notified : false } : t));
    setEditingId(null);
    setEditingText('');
  };

  const clearCompleted = () => {
    setTasks(prev => prev.filter(t => !t.completed));
  };

  // Recurring task handler: when completed, generate next occurrence
  const handleTaskComplete = (id) => {
    setTasks(prev => {
      const result = [];
      prev.forEach(t => {
        if (t.id === id) {
          result.push({ ...t, completed: true });
          if (t.recurrence && t.recurrence !== 'none' && t.due) {
            try {
              const dueDate = parseISO(t.due);
              let nextDue = null;
              if (t.recurrence === 'daily') nextDue = addDays(dueDate, 1).toISOString();
              else if (t.recurrence === 'weekly') nextDue = addWeeks(dueDate, 1).toISOString();
              else if (t.recurrence === 'monthly') nextDue = addMonths(dueDate, 1).toISOString();
              if (nextDue) {
                result.push({
                  id: Date.now() + Math.random(),
                  text: t.text,
                  completed: false,
                  due: nextDue,
                  recurrence: t.recurrence,
                  notified: false,
                  tags: t.tags || []
                });
              }
            } catch (e) { console.warn('Recurrence calc error', e); }
          }
        } else {
          result.push(t);
        }
      });
      return result;
    });
  };

  // Notifications: request permission and periodically check due tasks
  const enableNotifications = async () => {
    if (!('Notification' in window)) {
      alert('Browser Anda tidak mendukung Notification API');
      return;
    }
    try {
      const perm = await Notification.requestPermission();
      if (perm === 'granted') {
        setNotificationsEnabled(true);
      } else {
        alert('Izin notifikasi ditolak atau dibatalkan');
      }
    } catch (e) {
      console.warn('Notification permission error', e);
    }
  };

  useEffect(() => {
    let timer = null;
    if (notificationsEnabled) {
      // check every 30s
      timer = setInterval(() => {
        const now = new Date();
        setTasks(prev => prev.map(t => {
          if (t.due && !t.completed && !t.notified) {
            let dueDate;
            try { dueDate = parseISO(t.due); } catch { return t; }
            if (dueDate.getTime() <= now.getTime()) {
              try {
                if (Notification.permission === 'granted') {
                  /* eslint-disable no-new */
                  new Notification(`Tugas: ${t.text}`, { body: `Tenggat ${format(dueDate, 'Pp')}`, tag: `task-${t.id}` });
                }
              } catch (e) {
                console.warn('Failed sending notification', e);
              }
              return { ...t, notified: true };
            }
          }
          return t;
        }));
      }, 30000);
    }
    return () => { if (timer) clearInterval(timer); };
  }, [notificationsEnabled]);

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') addTask();
  };

  const filtered = tasks.filter(t => {
    if (filter === 'active') return !t.completed;
    if (filter === 'completed') return t.completed;
    return true;
  }).filter(t => filterTag ? (t.tags && t.tags.includes(filterTag)) : true)
    .filter(t => searchText ? t.text.toLowerCase().includes(searchText.toLowerCase()) : true);

  const remaining = tasks.filter(t => !t.completed).length;

  return (
    <div className="App">
      <h1>üìù To-Do List</h1>

      <div className="inputContainer">
        <input
          type="text"
          placeholder="Tambah tugas baru..."
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
          onKeyPress={handleKeyPress}
          className="input"
        />
        <input
          type="datetime-local"
          value={dueInput}
          onChange={(e) => setDueInput(e.target.value)}
          className="dueInput"
          title="Tanggal dan waktu tenggat (opsional)"
        />
        <select
          value={recurrenceInput}
          onChange={(e) => setRecurrenceInput(e.target.value)}
          className="selectRecurrence"
        >
          <option value="none">No Repeat</option>
          <option value="daily">Daily</option>
          <option value="weekly">Weekly</option>
          <option value="monthly">Monthly</option>
        </select>
        <input
          type="text"
          placeholder="Tags (comma-separated)"
          value={tagInput}
          onChange={(e) => setTagInput(e.target.value)}
          className="tagInput"
        />
        <button onClick={addTask} className="addBtn">
          Tambah
        </button>
      </div>

      <div className="toolbar">
        <div className="filters">
          <button className={filter === 'all' ? 'active' : ''} onClick={() => setFilter('all')}>All</button>
          <button className={filter === 'active' ? 'active' : ''} onClick={() => setFilter('active')}>Active</button>
          <button className={filter === 'completed' ? 'active' : ''} onClick={() => setFilter('completed')}>Completed</button>
        </div>
        <div className="tags-filter">
          {allTags.length > 0 && (
            <div className="tagsList">
              {allTags.map(tag => (
                <button
                  key={tag}
                  className={`tagBadge ${filterTag === tag ? 'active' : ''}`}
                  onClick={() => setFilterTag(filterTag === tag ? null : tag)}
                >
                  {tag}
                </button>
              ))}
            </div>
          )}
        </div>
        <div className="meta">
          <span>{remaining} left</span>
          <button className="clearBtn" onClick={clearCompleted}>Clear Completed</button>
          <button className="clearBtn" onClick={() => notificationsEnabled ? setNotificationsEnabled(false) : enableNotifications()}>{notificationsEnabled ? 'Disable Reminders' : 'Enable Reminders'}</button>
        </div>
      </div>

      <DragDropContext onDragEnd={(result) => {
        const { destination, source } = result;
        if (!destination) return;
        if (destination.index === source.index) return;
        setTasks(prev => {
          const copy = Array.from(prev);
          const [moved] = copy.splice(source.index, 1);
          copy.splice(destination.index, 0, moved);
          return copy;
        });
      }}>
        <Droppable droppableId="tasks">
          {(provided) => (
            <div className="tasksList" ref={provided.innerRef} {...provided.droppableProps}>
              {filtered.length === 0 ? (
                <p className="emptyMessage">Belum ada tugas. Tambahkan sekarang! üéØ</p>
              ) : (
                filtered.map((task, index) => (
                  <Draggable key={task.id} draggableId={String(task.id)} index={index}>
                    {(prov, snap) => (
                      <div
                        ref={prov.innerRef}
                        {...prov.draggableProps}
                        {...prov.dragHandleProps}
                        className={`taskItem ${task.completed ? 'completed' : ''} ${snap.isDragging ? 'dragging' : ''}`}>
                        <div className="left">
                          <input type="checkbox" checked={task.completed} onChange={() => toggleComplete(task.id)} />
                          {editingId === task.id ? (
                            <input
                              className="editInput"
                              value={editingText}
                              onChange={(e) => setEditingText(e.target.value)}
                              onKeyDown={(e) => e.key === 'Enter' && saveEdit(task.id)}
                              onBlur={() => saveEdit(task.id)}
                              autoFocus
                            />
                          ) : (
                            <div className="taskMain">
                              <span className="taskText" onDoubleClick={() => startEdit(task)}>{task.text}</span>
                              {task.tags && task.tags.length > 0 && (
                                <div className="taskTags">
                                  {task.tags.map(tag => (
                                    <span key={tag} className="taskTag">{tag}</span>
                                  ))}
                                </div>
                              )}
                              {task.due && (
                                <span className="dueText">{format(parseISO(task.due), 'Pp')}{task.recurrence && task.recurrence !== 'none' ? ` (${task.recurrence})` : ''}</span>
                              )}
                            </div>
                          )}
                        </div>
                        <button
                          onClick={() => deleteTask(task.id)}
                          className="deleteBtn"
                          title="Hapus tugas"
                        >
                          üóëÔ∏è
                        </button>
                      </div>
                    )}
                  </Draggable>
                ))
              )}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
}

export default App;
